 sec.sr_adm_stat_chg ab WHERE appl_status_new in (15\, 16) AND ab.system_key = a.system_key AND ab.appl_no = a.appl_no AND ab.appl_yr = a.appl_yr AND ab.appl_qtr = a.appl_qtr ) \, MailCode = mlc.mail_code FROM sec.student_1 v INNER JOIN sec.sr_adm_appl a ON v.system_key = a.system_key INNER JOIN sec.sr_adm_appl_req_col_major ar ON a.appl_yr = ar.appl_yr AND a.appl_qtr = ar.appl_qtr AND a.system_key = ar.system_key AND a.appl_no = ar.appl_no INNER JOIN sec.sr_major_code mc ON ar.req_major_abbr = mc.major_abbr LEFT OUTER JOIN sec.sr_adm_appl_mail_codes AS mlc ON v.system_key = mlc.system_key WHERE a.appl_yr = @year AND a.appl_qtr = @Quarter AND a.appl_branch = 1 AND CONVERT(char(1)\,a.appl_type) in (''2''\,''4''\,''5''\,''R'') --AND (a.trans_gpa is null or a.trans_gpa = 0) AND a.appl_status in (12\,15\,16) AND v.class <> 6 AND mc.major_pathway = 0','EDWAdminMart','No',0),
