us\, s.appl_stat_descrip\, addr.perm_line_1\, addr.perm_line_2\, addr.perm_city\, addr.perm_state\, addr.perm_zip_5\, addr.local_line_1\, addr.local_line_2\, addr.local_city\, addr.local_state\, addr.local_zip_5\, addr.perm_phone_num\, addr.local_phone_num\, addr.e_mail_ucs\, addr.e_mail_other\, confirmed_dt = (Select max(ab.trans_dt) from sec.sr_adm_stat_chg ab where ab.system_key = a.system_key and ab.appl_no = a.appl_no and ab.appl_yr = a.appl_yr and ab.appl_qtr = a.appl_qtr and ab.appl_status_new in (15\,16) )\, r.advising_date\, r.advising_type\, AdviseType = CASE WHEN r.advising_type= ''O'' THEN ''Orientation'' Else ''Other'' END\, v.running_start\, v.college_in_hs FROM sec.student_1 v inner join sec.sr_adm_appl a on v.system_key = a.system_key inner join sec.sr_adm_appl_req_col_major ar on a.appl_yr = ar.appl_yr and a.appl_qtr = ar.appl_qtr and a.appl_no = ar.appl_no and a.system_key = ar.system_key left join sec.sr_major_code mc on ar.req_major_abbr = mc.major_abbr and ar.req_pathway = mc.major_pathway inner join sec.sys_tbl_72_appl_status s on a.appl_status = s.table_key left join sec.addresses addr on v.system_key = addr.system_key LEFT JOIN sec.registration r ON v.system_key = r.system_key AND r.regis_yr=@year AND r.regis_qtr=@Quarter WHERE a.appl_branch = 1 and a.appl_type = ''1'' --and mc.major_pathway = 0 AND (a.appl_yr = @year) AND (a.appl_qtr = @Quarter) AND a.appl_status in (15\,16) order by v.student_name','EDWAdminMart','No',0),
('Report','MyFD Expense Transfer Volume Report','2027-09-03','2016-09-05',' Dan Schaaf','Dinah Mite Millikin & Eric Rucker','This report displays metrics for a selected date range about the volume of Expense Transfers initiated/executed in MyFinancialDesktop\, beginning with Dec-08-2008.','Services & Resources','Enterprise Reporting and Analytics','DECLARE @fundingtable table ( f_systemkey decimal(9\,0) \, Funding_Source varchar(30) ) INSERT INTO @fundingtable SELECT DISTINCT s1.system_key \, CASE WHEN mc.major_ss_inelig = 1 THEN ''Fee Based'' WHEN s1.system_key IN ( SELECT DISTINCT r1.system_key FROM UWSDBDataStore.sec.registration r1 INNER JOIN UWSDBDataStore.sec.registration_courses rc1 ON r1.system_key = rc1.system_key AND r1.regis_yr = rc1.regis_yr AND r1.regis_qtr = rc1.regis_qtr AND rc1.course_branch = 1 INNER JOIN UWSDBDataStore.sec.sys_tbl_39_calendar st39 ON ''0'' + CAST(rc1.regis_yr AS char(4)) + CAST(rc1.regis_qtr AS char(1)) = st39.table_key WHERE r1.enroll_status = 12 AND ((rc1.request_status IN(''A''\,''C''\,''R'')) OR (rc1.request_status IN(''W''\,''Y''\,''D'') AND rc1.request_dt > st39.tenth_day)) AND r1.regis_qtr = @Quarter AND r1.regis_yr = @year AND CAST(r1.regis_yr AS char(4)) + CAST(r1.regis_qtr AS char(1)) + rc1.crs_curric_abbr + CAST(rc1.crs_number AS char(3)) + rc1.crs_section_id IN ( ''20104B NURS350C''\, ''20104B NURS430D''\, --Fall 2010 ''20111B NURS407C''\, ''20111B NURS410C''\, --Winter 2011 ''20112B NURS403C''\, ''20112B HLTH497D''\, --Spring 2011 ''20113B HLTH497D''\,''20113B NURS409F''\,''20113B NURS409G''\,''20113B NURS409H''\,''20113B NURS495G''\,''20113B NURS495H''\,''20113B NURS495I''\, --Summer 2011 ''20114B NURS430D''\, ''20114B HLTH497D''\, ''20114B NURS350C''\, --Fall 2011 ''20121B NURS407C''\, ''20121B NURS410C''\, --Winter 2012 ''20122B NURS403C''\, ''20122B HLTH497C''\, --Spring 2012 ''20123B HLTH450A''\, ''20123B NURS409F''\, ''20123B NURS409G''\, ''20123B NURS409H''\, --Summer 2012 ''20124B HLTH497D''\, ''20124B NURS350C''\, ''20124B NURS430D''\, --Fall 2012 ''20131B NURS407C''\, ''20131B NURS410C''\, --Winter 2013 ''20132B NURS403C''\, ''20132B HLTH497C''\, --Spring 2013 ''20133B HLTH497A''\, ''20133B NURS409F''\, ''20133B NURS409G''\, ''20133B NURS409H'' --Summer 2013 ) ) THEN ''University Center Everett'' --NOTE there will need to be a separate WHEN...THEN statement for each year and quarter that the Everett Nursing program runs at least until Seattle comes up with some way to code them appropriately WHEN s1.system_key IN ( SELECT DISTINCT sg.system_key FROM UWSDBDataStore.sec.sr_student_grp sg WHERE sg.sg_group_cd = ''NEVT'' AND @year *10 + @Quarter BETWEEN sg.sg_init_yr *10 + sg.sg_init_qtr AND sg.sg_exp_yr *10 + sg.sg_exp_qtr ) THEN ''University Center Everett'' ELSE '' State Funded'' END FROM UWSDBDataStore.sec.student_1 s1 INNER JOIN UWSDBDataStore.sec.student_1_college_major s1cm ON s1.system_key = s1cm.system_key AND s1cm.branch = 1 AND s1cm.index1 = 1 INNER JOIN UWSDBDataStore.sec.registration r ON s1.system_key = r.system_key INNER JOIN UWSDBDataStore.sec.Sr_major_code mc ON s1cm.major_abbr = mc.major_abbr AND s1cm.pathway = mc.major_pathway AND mc.major_last_yr IN ( SELECT MAX(mc1.major_last_yr) AS lastyr FROM UWSDBDataStore.sec.Sr_major_code mc1 WHERE s1cm.major_abbr = mc1.major_abbr AND s1cm.pathway = mc1.major_pathway ) WHERE r.regis_yr = @year AND r.regis_qtr = @Quarter AND r.enroll_status = 12 ----------------------------------------------------------------------------------------------------- --Headcount SELECT Dept = CASE WHEN scm.major_abbr = ''B NM'' THEN ''Non-Matric'' WHEN scm.major_abbr = ''B BUSX'' THEN ''BUSINESS ADMINISTRATION (BELLEVUE)'' WHEN scm.major_abbr = ''B NURS'' AND ft.funding_source =''University Center Everett'' THEN ''NURSING (EVERETT)'' WHEN m.major_dept IN(865\, 1073\, 1091\, 1092\, 1093) THEN ''School of Science\, Technology\, Engr & Math'' ELSE d.dept_full_nm END \, Funding = ft.Funding_Source \, r.regis_qtr \, r.regis_yr \, class_status