le_key left join UWSDBDataStore.sec.addresses addr on s1.system_key = addr.system_key LEFT JOIN sec.sys_tbl_30_highschool st30hs ON a.high_sch_code = st30hs.table_key WHERE appl_yr IN (@Year) AND appl_qtr IN (@Quarter) AND appl_branch = @branch AND appl_type = ''1'' AND high_sch_gpa >= @GPA AND ( CASE WHEN s1.s1_high_act > 0 THEN s1.s1_high_act ELSE NULL END >= @ACT OR CASE WHEN s1.s1_high_satv > 0 THEN s1.s1_high_satv ELSE NULL END >= @SATV OR CASE WHEN s1.s1_high_satm > 0 THEN s1.s1_high_satm ELSE NULL END >= @SATM )','EDWAdminMart','No',0),
