e'' END AS Accepted \, CASE WHEN ( select appl_branch from sec.sr_adm_appl b where sr_adm_appl.appl_yr =b.appl_yr and sr_adm_appl.appl_qtr = b.appl_qtr and sr_adm_appl.system_key = b.system_key and b.appl_status = 16 ) = 0 THEN ''Seattle'' WHEN ( select appl_branch from sec.sr_adm_appl c where sr_adm_appl.appl_yr = c.appl_yr and sr_adm_appl.appl_qtr = c.appl_qtr and sr_adm_appl.system_key = c.system_key and c.appl_status = 16 ) = 1 THEN ''Bothell'' WHEN ( select appl_branch from sec.sr_adm_appl d where sr_adm_appl.appl_yr = d.appl_yr and sr_adm_appl.appl_qtr = d.appl_qtr and sr_adm_appl.system_key = d.system_key and d.appl_status = 16 ) = 2 THEN ''Tacoma'' ELSE ''None'' END AS Confirmed \,HST_Received = appl_doc_rcvd_dt \,Current_Credits = registration.current_credits \,mpt.test_dt \,mpt.test_type \,mpt.test_score FROM sec.student_1 v INNER JOIN sec.sr_adm_appl ON v.system_key = sr_adm_appl.system_key INNER JOIN sec.registration ON v.system_key = registration.system_key INNER JOIN sec.registration_regis_col_major rcm ON registration.system_key = rcm.system_key AND registration.regis_yr = rcm.regis_yr AND registration.regis_qtr = rcm.regis_qtr INNER JOIN sec.addresses addr ON addr.system_key = v.system_key LEFT JOIN ( SELECT * FROM sec.sr_adm_applicant_docs WHERE appl','EDWAdminMart','No',0),
